<h1>Group edit - <?php  echo htmlspecialchars($group->name)?></h1><?php  if (isset($errors)) : ?><?php  if (isset($errors)) : ?><div data-alert class="alert alert-danger alert-dismissible fade in"><button type="button" class="close" data-dismiss="alert" aria-label="Close"><span aria-hidden="true">&times;</span></button><ul class="validation-ul"><?php  foreach ($errors as $err) : ?><li><?php  echo $err?></li><?php  endforeach;?></ul></div><?php  endif;?><?php  endif; ?><div><form action="/richard/sites/live121support.com-master/index.php/site_admin/user/editgroup/<?php  echo $group->id?>" method="post"><input type="hidden" name="csfr_token" value="<?php  echo erLhcoreClassUser::instance()->getCSFRToken()?>" /><div class="form-group"><label>Title</label><input class="form-control" type="text" name="Name"  value="<?php  echo htmlspecialchars($group->name);?>" /></div><div class="form-group"><label>Member of this group can work with the following groups</label><select multiple="multiple" size="5" class="form-control" name="MemberGroup[]"><?php   $assignedGrupsIds = array(); foreach ($group_work as $groupId) { $assignedGrupsIds[] = $groupId->group_work_id; } foreach (erLhcoreClassModelGroup::getList() as $groupMember) : ?><option value="<?php  echo $groupMember->id?>"<?php  in_array($groupMember->id, $assignedGrupsIds) ? print ' selected="selected" ' : ''?>><?php  echo htmlspecialchars($groupMember->name)?></option><?php  endforeach; ?></select></div><div class="form-group"><label>Disabled <input type="checkbox" name="Disabled"  value="on" <?php  echo $group->disabled == 1 ? 'checked="checked"' : ''?> /></label></div><input type="submit" class="btn btn-default" name="Update_group" value="Update"/></form></div><h4>Assigned users - <?php  echo htmlspecialchars($group->name)?></h4><form action="/richard/sites/live121support.com-master/index.php/site_admin/user/editgroup/<?php  echo $group->id?>" method="post"><input type="hidden" name="csfr_token" value="<?php  echo erLhcoreClassUser::instance()->getCSFRToken()?>" /><table class="table" cellpadding="0" cellspacing="0"><thead><tr><th width="1%">&nbsp;</th><th>Username</th></tr></thead><?php  foreach ($users as $UserAssigned) : ?><tr><td><input type="checkbox" name="AssignedID[]" value="<?php  echo $UserAssigned->id?>" /></td><td><?php  echo $UserAssigned->user?></td></tr><?php  endforeach;?></table><?php  if (isset($pages)) : ?><?php  if (isset($pages) && $pages->num_pages > 1) : ?><nav><ul class="pagination paginator-lhc"><?php  if ($pages->current_page != 1) : ?><li class="arrow"><a class="previous" href="<?php  echo $pages->serverURL,$pages->prev_page,$pages->querystring?>">&laquo;</a></li><?php  endif;?><?php  if ($pages->num_pages > 10) : $needNoBolder = false; if ($pages->range[0] > 1) : $i = 1; $pageURL = $i > 1 ? '/(page)/'.$i : ''; $needNoBolder = true; if ($i == $pages->current_page) : ?><li class="current no-b"><a title="Go to page <?php  echo $i?> of <?php  echo $pages->num_pages?>" href="#"><?php  echo $i?></a></li><?php  else : ?><li><a class="no-b" title="Go to page <?php  echo $i?> of <?php  echo $pages->num_pages?>" href="<?php  echo $pages->serverURL,$pageURL,$pages->querystring?>"><?php  echo $i?></a></li><?php  endif; ?><li><a href="#">...</a></li><?php endif; for($i=$pages->range[0];$i<=$pages->lastArrayNumber;$i++) : if ($i > 0) : $pageURL = $i > 1 ? '/(page)/'.$i : ''; $noBolderClass = ($i == 1 || $needNoBolder == true) ? ' no-b' : ''; $needNoBolder = false; if ($i == $pages->current_page): ?><li class="active<?php  echo $noBolderClass?>"><a title="Go to page <?php  echo $i?> of <?php  echo $pages->num_pages?>"  href="#"><?php  echo $i?></a></li><?php  else : ?><li><a class="<?php  echo $noBolderClass?>" title="Go to page <?php  echo $i?> of <?php  echo $pages->num_pages?>" href="<?php  echo $pages->serverURL,$pageURL,$pages->querystring?>"><?php  echo $i?></a></li><?php  endif;endif;endfor; if ($pages->lastArrayNumber < $pages->num_pages) : $i = $pages->num_pages; $pageURL = $i > 1 ? '/(page)/'.$i : ''; ?><li><a href="#">...</a></li><?php  if ($i == $pages->current_page) : ?><li class="active"><a title="Go to page <?php  echo $i?> of <?php  echo $pages->num_pages?>" href="#"><?php  echo $i?></a></li><?php   else : ?><li><a class="no-b" title="Go to page <?php  echo $i?> of <?php  echo $pages->num_pages?>" href="<?php  echo $pages->serverURL,$pageURL,$pages->querystring?>"><?php  echo $i?></a></li><?php  endif; endif; else : for ($i=1;$i<=$pages->num_pages;$i++) : $noBolderClass = ($i == 1) ? ' no-b' : ''; $pageURL = $i > 1 ? '/(page)/'.$i : ''; if ($i == $pages->current_page) :?><li class="active<?php  echo $noBolderClass?>"><a href="#"><?php  echo $i?></a></li><?php  else : ?><li><a class="paginate" href="<?php  echo $pages->serverURL,$pageURL,$pages->querystring;?>"><?php  echo $i?></a></li><?php  endif; endfor; endif; if ($pages->current_page != $pages->num_pages): ?><li class="arrow"><a class="next" href="<?php  echo $pages->serverURL,'/(page)/',$pages->next_page,$pages->querystring?>">&raquo;</a></li><?php  endif;?></ul><div class="found-total pull-right">Page <?php  echo $pages->current_page?> of <?php  echo $pages->num_pages?>, Found - <?php  echo $pages->items_total?></div></nav><?php  endif;?><?php  endif;?><input type="submit" class="btn btn-danger" name="Remove_user_from_group" value="Remove user from the group" /><input class="btn btn-default" type="button" value="Assign user" onclick="lhc.revealModal({'iframe':true,'height':600,'url':'/richard/sites/live121support.com-master/index.php/site_admin/user/groupassignuser/<?php  echo $group->id?>'})" /></form><h4>Assigned roles - <?php  echo htmlspecialchars($group->name)?></h4><form action="/richard/sites/live121support.com-master/index.php/site_admin/user/editgroup/<?php  echo $group->id?>" method="post"><input type="hidden" name="csfr_token" value="<?php  echo erLhcoreClassUser::instance()->getCSFRToken()?>" /><table class="table" cellpadding="0" cellspacing="0"><thead><tr><th width="1%">&nbsp;</th><th>Name</th></tr></thead><?php  foreach (erLhcoreClassGroupRole::getGroupRoles($group->id) as $UserAssigned) : ?><tr><td><input type="checkbox" name="AssignedID[]" value="<?php  echo $UserAssigned['assigned_id']?>" /></td><td><?php  echo htmlspecialchars($UserAssigned['name'])?></td></tr><?php  endforeach; ?></table><input type="submit" class="btn btn-danger" name="Remove_role_from_group" value="Remove role from group" /><input class="btn btn-default" type="button" value="Assign role" onclick="lhc.revealModal({'url':'/richard/sites/live121support.com-master/index.php/site_admin/permission/groupassignrole/<?php  echo $group->id?>'});" /></form><?php  if (isset($adduser)) : ?><script type="text/javascript">$(function() {lhc.revealModal({'iframe':true,'height':600,'url':'/richard/sites/live121support.com-master/index.php/site_admin/user/groupassignuser/<?php  echo $group->id?>'});})</script><?php  endif; ?>