<h1>New group</h1><?php  if (isset($errors)) : ?><?php  if (isset($errors)) : ?><div data-alert class="alert alert-danger alert-dismissible fade in"><button type="button" class="close" data-dismiss="alert" aria-label="Close"><span aria-hidden="true">&times;</span></button><ul class="validation-ul"><?php  foreach ($errors as $err) : ?><li><?php  echo $err?></li><?php  endforeach;?></ul></div><?php  endif;?><?php  endif; ?><form action="/richard/sites/live121support.com-master/index.php/site_admin/user/newgroup" method="post"><div class="form-group"><label>Title</label><input type="text" name="Name" class="form-control" value="" /></div><div class="form-group"><label>Member of this group can work with the following groups</label><select multiple="multiple" size="5" class="form-control" name="MemberGroup[]"><?php   $assignedGrupsIds = array(); if (isset($_POST['MemberGroup'])) { $assignedGrupsIds = $_POST['MemberGroup']; } foreach (erLhcoreClassModelGroup::getList() as $groupMember) : ?><option value="<?php  echo $groupMember->id?>"<?php  in_array($groupMember->id, $assignedGrupsIds) ? print ' selected="selected" ' : ''?>><?php  echo htmlspecialchars($groupMember->name)?></option><?php  endforeach; ?></select></div><div class="form-group"><label>Disabled <input type="checkbox" name="Disabled"  value="on" <?php  echo $group->disabled == 1 ? 'checked="checked"' : ''?> /></label></div><div class="btn-group" role="group" aria-label="..."><input type="submit" class="btn btn-default" name="Save_group" value="Save"/><input type="submit" class="btn btn-default" name="Save_group_and_assign_user" value="Save and assign the user"/></div><input type="hidden" name="csfr_token" value="<?php  echo erLhcoreClassUser::instance()->getCSFRToken()?>" /></form>